import React from "react";

const Pokeinfo = () => {
    return (
        <> 
          <div className="">
              <h1 className="uppercase font-semibold tracking-wide">Charmandar</h1>
              <img src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/4.svg" alt="img"  className="h-[160px] m-12"/>
              <div className="abilities w-[90%] bg-slate-50 flex m-auto justify-center items-center ">
                 <div className="group">blaze</div>
                <div className="group">solar-power</div>
              </div>
               
              <div className="base-stat">
                <h3>Hp:30</h3>
                <h3>Attack:52</h3>
                <h3>Defence : 43</h3>
                <h3>special-attack:50</h3>
                <h3>speed</h3>
                 
              </div>
          </div>
        </>
    )
}

export default Pokeinfo
